import React from "react";
import Col from "react-bootstrap/Col";
import Card from "react-bootstrap/Card";

export class Offres extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hovered: false };
  }
  render() {
    return (
      <Col md lg="3">
        <Card
        //   style={{ width: "12rem", backgroundColor: "#282c34" }}
        //   className="fluid"
        >
          <Card.Body>
            <Card.Title>
              <h3 className="mb-2 text-muted">{this.props.domaine}</h3>
            </Card.Title>
            <Card.Subtitle>
              <h4 className="mb-2 text-muted small">{this.props.entreprise}</h4>
            </Card.Subtitle>
            <Card.Content>
              <p>{this.props.experience}</p>
              <p>{this.props.description}</p>
            </Card.Content>
          </Card.Body>
        </Card>
      </Col>
    );
  }
}
