import React from "react";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import { useState, useEffect } from "react";
// import JsonData from "../data/data.json";
import "../index.css";
import Home from "../views/home";
import StagesOffres from "../views/stages-offres";
import StagesDemandes from "../views/stages-demandes";
import Inscription from "../views/inscription";
import Administration from "../views/administration";

export const Navigation = () => {
  // const [landingPageData, setLandingPageData] = useState({});
  // useEffect(() => {
  //   setLandingPageData(JsonData);
  // }, []);

  const [landingPageData, setLandingPageData] = useState(null);
  useEffect(() => {
    fetch("http://localhost:8000/landingPageData")
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        console.log(data, "!!!!!!!!!!!!!!!!!");
        setLandingPageData(data);
      });
  }, []);

  return (
    <>
      <Router>
        {landingPageData && (
          <div>
            <nav id="menu" class="navbar navbar-default navbar-expand-lg">
              <div className="container ">
                <a class="navbar-brand" href="#page-top">
                  e Stage
                </a>
                <button
                  class="navbar-toggler"
                  type="button"
                  data-toggle="collapse"
                  data-target="#navbarTogglerDemo02"
                  aria-controls="navbarTogglerDemo02"
                  aria-expanded="false"
                  aria-label="Toggle navigation"
                >
                  <span class="navbar-toggler-icon"></span>
                </button>
                <div class="collapse navbar-collapse" id="navbarTogglerDemo02">
                  <ul
                    class="navbar-nav mr-auto mt-2 mt-lg-0"
                    className="nav navbar-nav"
                  >
                    <li class="nav-item nav navbar-nav">
                      <Link class="nav-link" to="/home">
                        À propos
                      </Link>
                    </li>
                    <li class="nav-item nav navbar-nav">
                      <Link class="nav-link" to="/stages-offres">
                        Offres de stages
                      </Link>
                    </li>
                    <li class="nav-item nav navbar-nav">
                      <Link class="nav-link" to="/stages-demandes">
                        Demande de stagiaire
                      </Link>
                    </li>
                    <li class="nav-item nav navbar-nav">
                      <Link class="nav-link" to="/inscription">
                        Inscription
                      </Link>
                    </li>
                    <li>
                      <Link class="nav-link" to="/administration">
                        Administration
                      </Link>
                    </li>
                  </ul>
                  {/* <form class="form-inline my-2 my-lg-0">
              <input
                class="form-control mr-sm-2"
                type="search"
                placeholder="Search"
              ></input>
              <button
                class="btn btn-outline-success my-2 my-sm-0"
                type="submit"
              >
                Search
              </button>
            </form> */}
                </div>
              </div>
            </nav>
            <Switch>
              <Route path="/stages-offres">
                <StagesOffres data={landingPageData.Stages} />
              </Route>
              <Route path="/stages-demandes">
                <StagesDemandes data={landingPageData.Stages} />
              </Route>
              <Route path="/inscription">
                <Inscription />
              </Route>
              <Route path="/administration">
                <Administration data={landingPageData.Testimonials} />
              </Route>
              <Route path="/home">
                <Home />
              </Route>
            </Switch>
          </div>
        )}
      </Router>
    </>
  );
};
