const StagesDemandes = (props) => {
  return (
    <div id="stages">
      <div className="container-fluid">
        <div className="row">
          <div className="col-md-8 col-md-offset-4">
            <Form inline>
              <FormControl
                className="search-bar"
                type="text"
                placeholder="Trouver un stage"
                className="mr-sm-2"
              />
              <Button variant="">Search</Button>
            </Form>
          </div>
          <div className="col-xs-12 col-md-8">
            <h2>
              Listes de stagiaires<small> Profils</small>
            </h2>
            <div className="row">
              {props.data
                ? props.data.candidats.map((d, i) => (
                    <div key={`${d}-${i}`} className="col-md-4">
                      <div className="testimonial">
                        <div className="testimonial-meta"> - {d.nom} </div>
                        <div className="testimonial-content">
                          <p>"{d.experience}"</p>
                        </div>
                        <div className="testimonial-meta"> - {d.domaine} </div>
                        <div className="testimonial-content">
                          <p>"{d.motivation}"</p>
                        </div>
                      </div>
                    </div>
                  ))
                : "loading"}
            </div>
