import React from "react";
import { Link } from "react-router-dom";
import "./layout/menu.css";
//
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import { Login } from "../components/login";
import Administration from "./administration";
import { Deconnexion } from "../components/deconnexion";

export class Menu extends React.Component {
  constructor(props) {
    super(props);
    this.state = { connecter: false };
    this.gererConnexion = this.gererConnexion.bind(this);
    this.verifierConnexion = this.verifierConnexion.bind(this);
  }

  verifierConnexion(connexion) {
    this.setState({ connecter: connexion });
  }

  gererConnexion() {
    if (this.state.connecter === "etudiant@gmail.com") {
      return (
        <aside className="Menu">
          <nav>
            <ul>
              <li>
                <Link to="/accueil-admin">Accueil</Link>
              </li>
              <li>
                <Link to="/liste-offres">Étudiants</Link>
              </li>
              <li>
                <Link to="/ajouter-stage">Ajouter un candidat</Link>
              </li>
              <li>
                <Deconnexion />
              </li>
            </ul>
          </nav>
        </aside>

        // <Container fluid>
        //   <Administration />
        //   <Row className="d-flex justify-content-start">
        //     <Col className="text-center">
        //       <Deconnexion onClick={this.verifierConnexion} />
        //     </Col>
        //   </Row>
        // </Container>
      );
    }
    if (this.state.connecter === "entreprise@gmail.com") {
      return (
        <aside className="Menu">
          <nav>
            <ul>
              <li>
                <Link to="/accueil-admin">Accueil</Link>
              </li>
              <li>
                <Link to="/liste-candidats">Entreprises</Link>
              </li>
              <li>
                <Link to="/ajouter-offre">Ajouter un offre</Link>
              </li>
              <li>
                <Deconnexion />
              </li>
            </ul>
          </nav>
        </aside>
      );
    } else {
      return <Login onClick={this.verifierConnexion} />;
    }
  }
  render() {
    return this.gererConnexion();
  }
}

export default Menu;

