import React from "react";
import { Form, Button, FormControl } from "react-bootstrap";
import ListGroup from "react-bootstrap/ListGroup";

const StagesOffres = (props) => {
  return (
    <div id="stages">
      <div className="container-fluid">
        <div className="row">
          <div className="col-md-8 col-md-offset-4">
            <Form inline>
              <FormControl
                className="search-bar"
                type="text"
                placeholder="Trouver un stage"
                className="mr-sm-2"
              />
              <Button variant="">Search</Button>
            </Form>
          </div>
          <div className="col-xs-12 col-md-8">
            <h2>Liste de stages</h2>
            {props.data ? props.data.title : "Loading"}
          </div>
          <div className="col-xs-12 col-md-4">
            <div className="list-style">
              <h2>Secteur d'activit√©</h2>
              <ListGroup>
                {props.data
                  ? props.data.domaine.map((d, i) => (
                      <ListGroup.Item key={`${d}-${i}`} className="bgList">
                        {" "}
                        {d}
                      </ListGroup.Item>
                    ))
                  : "loading"}
              </ListGroup>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default StagesOffres;
