import React from "react";
import { Form, Button, FormControl } from "react-bootstrap";
import Home from "./home";
import { useState, useEffect } from "react";
import ListGroup from "react-bootstrap/ListGroup";

const StagesOffres = (props) => {
  const [searchTerm, setSearchTerm] = useState("");
  useEffect(() => {
    fetch("http://localhost:8000/offres")
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setSearchTerm(data);
      });
  }, []);
  return (
    <div id="stages">
      <div className="container">
        <div className="row">
          <div className="col-12 mt-3">
            <h2>
              Liste de stages<small> Opportunités</small>
            </h2>
            <input
              type="text"
              placeholder="Rechercher"
              onChange={(event) => {
                setSearchTerm(event.target.value);
              }}
            />
          </div>
          <div className="row">
            {data
              .filter((val) => {
                if (setSearchTerm == "") {
                  return val;
                } else if (
                  val.domaine
                    .toLowerCase()
                    .includes(setSearchTerm.toLowerCase())
                ) {
                  return val;
                }
              })
              .map((val, key) => {
                return (
                  <div className="testimonial">
                    <div className="testimonial-content">
                      <h3>{key.domaine}</h3>
                      <h4>
                        <small>entreprise:</small>
                        {key.entreprise}
                      </h4>
                      <p>
                        <small>- experience:</small>
                        {key.experience}
                      </p>
                      <div className="testimonial-meta">{key.description}</div>
                    </div>
                  </div>
                );
              })}
          </div>
        </div>
      </div>
      <Home />
    </div>
  );
};

export default StagesOffres;

