const StagesDemandes = (props) => {
  const [landingPageData, setLandingPageData] = useState({});
  useEffect(() => {
    setLandingPageData(JsonData);
  }, []);

  return (
    <div id="stages">
      <div className="container-fluid">
        <div className="row">
          <div className="col-md-8 col-md-offset-4">
            <Form inline>
              <FormControl
                className="search-bar"
                type="text"
                placeholder="Trouver un stage"
                className="mr-sm-2"
              />
              <Button variant="">Search</Button>
            </Form>
          </div>
          <div className="col-xs-12 col-md-8">
            <h2>Listes de stagiaires</h2>
            <div className="row">
              {props.data
                ? props.data.map((d, i) => (
                    <div key={`${d}-${i}`} className="col-md-4">
                      <div className="testimonial">
                        <div className="testimonial-content">
                          <h3>{d.domaine}</h3>
                          <h4>
                            <small>entreprise:</small>
                            {d.entreprise}
                          </h4>
                          <p>
                            <small>- experience:</small>
                            {d.experience}
                          </p>
                          <div className="testimonial-meta">
                            {d.description}
                          </div>
                        </div>
                      </div>
                    </div>
                  ))
                : "loading"}
            </div>
          </div>
          <div className="col-xs-12 col-md-4">
            <div className="list-style">
              <h2>Secteur d'activité</h2>
              <ListGroup>
                {props.data
                  ? props.data.domaine.map((d, i) => (
                      <ListGroup.Item key={`${d}-${i}`} className="bgList">
                        {" "}
                        {d}
                      </ListGroup.Item>
                    ))
                  : "loading"}
              </ListGroup>
            </div>
          </div>
        </div>
      </div>
      <Home />
    </div>
  );
};

export default StagesDemandes;
