  },
  {
    entreprise: "Back to Black",
    domaine: "Programmation, Développement",
    experience: "6 mois",
    description:
      "Consectetur adipiscing elit. Duis sed dapibus leo nec ornare diam sedasd commodo nibh ante facilisis ",
  },
  {
    entreprise: "Poker Face",
    domaine: "Programmation, Développement",
    experience: "4 mois",
    description:
      "Consectetur adipiscing elit. Duis sed dapibus leo nec ornare diam sedasd commodo nibh ante facilisis ",
  },
];
export class StagesDemandes extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      rechercher: "",
      listeOffres: offresDeStages,
      cestDansLaListe: false,
    };
    this.Rechercher = this.Rechercher.bind(this);
    this.Effacer = this.Effacer.bind(this);
    this.HandleOffres = this.HandleOffres.bind(this);
  }
  HandleOffres(index) {
    console.log(this.state.listeOffres[index].domaine);
    this.setState({
      cestDansLaListe: true,
      liste: this.state.listeOffres[index],
    });
  }

  Rechercher() {
    let domaine = document.getElementById("inputRechercher").value;
    this.setState({ rechercher: domaine });
  }
  Effacer() {
    document.getElementById("inputRechercher").value = "";
    this.setState({ rechercher: "" });
  }
  render() {
    return (
      <div id="stages">
        <div className="container-fluid">
          <div className="row">
            <div className="col-md-8 col-md-offset-4">
              <Form inline>
                <InputGroup size="sm" className="mb-3">
                  <FormControl
                    className="search-bar mr-sm-2"
                    placeholder="Rechercher"
                    aria-label="Rechercher"
                    aria-describedby="Rechercher"
                    onChange={this.Rechercher}
                  />
                  <InputGroup.Append>
                    <Button
                      variant="outline-secondary"
                      onClick={this.Rechercher}
                    >
                      Rechercher
                    </Button>
                  </InputGroup.Append>
                  <InputGroup.Append>
                    <Button variant="outline-secondary" onClick={this.Effacer}>
                      Annuler
                    </Button>
                  </InputGroup.Append>
                </InputGroup>
                {/* </Form>
              <Form inline>
                <FormControl
                  className="search-bar"
                  type="text"
                  placeholder="Trouver un stage"
                  className="mr-sm-2"
                />
                <Button variant="">Search</Button>*/}
              </Form>
            </div>
            <div className="col-xs-12 col-md-8">
              <h2>Listes de stagiaires</h2>
              {/* {props.data ? props.data.title : "Loading"} */}
              {this.AfficherAlbums()}
            </div>
            <div className="col-xs-12 col-md-4">
              <div className="list-style">
                <h2>Secteur d'activité</h2>
                <ListGroup>
                  {/* {props.data
                    ? props.data.domaine.map((d, i) => (
                        <ListGroup.Item key={`${d}-${i}`} className="bgList">
                          {" "}
                          {d}
                        </ListGroup.Item>
                      ))
                    : "loading"} */}
                </ListGroup>
              </div>
            </div>
          </div>
        </div>
        <Home />
      </div>
    );
  }

  AfficherAlbums() {
    if (this.state.rechercher === "") {
      // Chaque fois que l'utilisateur clique sur l'album, il envoie l'index à HandleAlbum
      return this.state.listeOffres.map((element, i) => (
        <div onClick={(e) => this.HandleOffres(i)}>
          <Offres
            className=""
            key={"perso" + i}
            domaine={element.domaine}
            entreprise={element.entreprise}
            experience={element.experience}
            description={element.description}
          ></Offres>
        </div>
      ));
    } else {
      const searchResult = this.state.listeOffres.filter((liste) => {
        const domaine = liste.domaine.toLowerCase();
        const entreprise = liste.entreprise.toLowerCase();
        const experience = liste.experience.toLowerCase();
        const description = liste.description.toLowerCase();
        const rechercheString = this.state.rechercher.toLowerCase();
        return (
          domaine.includes(rechercheString) ||
          entreprise.includes(rechercheString) ||
          experience.includes(rechercheString) ||
          description.includes(rechercheString)
        );
      });
      return searchResult.map((element, i) => (
        <div onClick={(e) => this.HandleOffres(i)}>
          <Offres
            className=""
            key={"perso" + i}
            domaine={element.domaine}
            entreprise={element.entreprise}
            experience={element.experience}
            description={element.description}
          ></Offres>
        </div>
      ));
    }
  }
}
export default StagesDemandes;
